import { useState } from "react";
import React from "react";

var tasks1 = [
    {
        id: 1,
        title: "Attend meeting",
        status: 0,
    },
    {
        id: 2,
        title: "Do laundry",
        status: 1,
    },
    {
        id: 3,
        title: "Bring medicine",
        status: 1,
    },
    {
        id: 4,
        title: "Do Dancing",
        status: 0,
    }
];

function Hum() {
    const [task, setTask] = useState({});
    const [tasks, setTasks] = useState(tasks1);

    const changeStatus = (index) => {
        let newTasks = [...tasks];
        newTasks[index].status = !newTasks[index].status;
        setTasks(newTasks)
    }

    return (
        <div>


            <h3>Status:</h3>
            <input name></input>
            <ul>
                {tasks.map((item, index) => {
                    return (
                        <li style={{ textDecoration: (item.status ? "line-through" : "") }} >
                            {item.id} - {item.title} <button onClick={() => { changeStatus(index) }}> Completed</button>
                        </li>
                    );
                })}
            </ul>
        </div>
    );
}
export default Hum;
