import React,{useState} from "react";


const Calculator =() => {

    const [message, setMessage] = useState('');

  const handleChange = event => {
    setMessage(event.target.value);

    console.log('value is:', event.target.value);
  };

  const handleClick = event => {
    event.preventDefault();

    // 👇️ value of input field
    console.log('handleClick 👉️', message);
  };



    return(

<div>

    <div style={{backgroundColor:'black',height:'400px',width:'400px',
                display:'flex',flexDirection:'column',flex:'1'}}>

                    <div style={{backgroundColor:'green',flex:'0.25'}}>
<input></input>
                </div>

                <div style={{backgroundColor:'pink',flex:'0.15'}}>

                    <button style={{height:'60px',width:'100px',fontSize:'20px'}}>C</button>
                    <button style={{height:'60px',width:'100px',fontSize:'20px'}}>()</button>
                    <button style={{height:'60px',width:'100px',fontSize:'20px'}}>%</button>
                    <button style={{height:'60px',width:'100px',fontSize:'20px'}}>÷</button>



                </div>

                <div style={{backgroundColor:'blue',flex:'0.15'}}>

                <button style={{height:'60px',width:'100px',fontSize:'20px'}}>7</button>
                    <button style={{height:'60px',width:'100px',fontSize:'20px'}}>8</button>
                    <button style={{height:'60px',width:'100px',fontSize:'20px'}}>9</button>
                    <button style={{height:'60px',width:'100px',fontSize:'20px'}}>X</button>

                </div>

                <div style={{backgroundColor:'red',flex:'0.15'}}>

                <button style={{height:'60px',width:'100px',fontSize:'20px'}}>4</button>
                    <button style={{height:'60px',width:'100px',fontSize:'20px'}}>5</button>
                    <button style={{height:'60px',width:'100px',fontSize:'20px'}}>6</button>
                    <button style={{height:'60px',width:'100px',fontSize:'20px'}}>-</button>

                </div>

                <div style={{backgroundColor:'yellow',flex:'0.15'}}>

                <button style={{height:'60px',width:'100px',fontSize:'20px'}}>  1</button>
                    <button style={{height:'60px',width:'100px',fontSize:'20px'}}>2</button>
                    <button style={{height:'60px',width:'100px',fontSize:'20px'}}>3</button>
                    <button style={{height:'60px',width:'100px',fontSize:'20px'}}>+</button>

                </div>

                <div style={{backgroundColor:'grey',flex:'0.15'}}>

                <button style={{height:'60px',width:'100px',fontSize:'20px'}}>0</button>
                    <button style={{height:'60px',width:'100px',fontSize:'20px'}}>00</button>
                    <button style={{height:'60px',width:'100px',fontSize:'20px'}}>.</button>
                    <button style={{height:'60px',width:'100px',fontSize:'20px'}}>=</button>

                </div>







    </div>

</div>

    )
}

export default  Calculator;
