import React, { useState } from "react";

const Incre = ()=> {

  const [count, setCount] = useState(0);

  const countManagement = (sign) => {
    let val = count + (sign?1:-1);
    if(val<0)
      return;
    setCount(val);
  };

  return (
    <div >
      <button style={"color:red"} onClick={()=>countManagement(0)}>decrementCount</button>
      {count}
      <button onClick={()=>countManagement(1)}>Increment</button>
    </div>
  );
}

export default Incre;

